defmodule Ton.Boc.HeaderTest do
  use ExUnit.Case

  alias Ton.Boc.Header

  describe "parse_header/1" do
    test "parses a header" do
      {:ok, source} =
        Base.decode64(
          ""
        )

      assert %Ton.Boc.Header{
               has_idx: false,
               hash_crc32: false,
               has_cache_bits: false,
               flags: 0,
               size_bytes: 1,
               off_bytes: 2,
               cells_num: 20,
               roots_num: 1,
               absent_num: 0,
               tot_cells_size: 724,
               root_list: [0],
               index: [],
               cells_data: cells_data
             } = Header.parse(source)

      expected_cells_data =
        Base.decode16!(
          "",
          case: :lower
        )

      assert expected_cells_data == cells_data
    end
  end
end
