defmodule Ton.Cell.TopologicalOrderTest do
  use ExUnit.Case

  alias Ton.Boc
  alias Ton.Cell.TopologicalOrder

  @cell Base.decode64!(
          ""
        )
        |> Boc.parse()
        |> Enum.at(0)

  describe "sort/1" do
    test "sorts cells" do
      refs = @cell |> TopologicalOrder.sort() |> Enum.map(fn cell -> cell.refs end)

      expected_refs = [
        [1],
        [7, 2],
        [6, 5, 4, 3],
        [],
        [],
        [],
        [],
        [17, 8],
        [10, 9],
        [],
        [12, 11],
        [],
        [16, 13],
        [15, 14],
        [],
        [],
        [],
        [19, 18],
        [],
        []
      ]

      assert expected_refs == refs
    end
  end
end
